    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed

        double FC=33.8;
        if (jComboBox1.getSelectedItem().equals("Celsius TO Fehrenhiet"))
        {
             double c = Double.parseDouble(jTextField1.getText());
             double f = c*FC;
             String value=String.format("째F %.2f", f);
             jTextField2.setText(value);      
        }
        if (jComboBox1.getSelectedItem().equals("Celsius TO Kelvin"))
        {
             double c = Double.parseDouble(jTextField1.getText());
             double k = c + 273.15;
             String value=String.format("K %.2f", k);
             jTextField2.setText(value);      
        }
         if (jComboBox1.getSelectedItem().equals("Fehrenhiet TO Celsius"))
        {
             double f = Double.parseDouble(jTextField1.getText());
             double c = (f-32)*0.55;
             String value=String.format("째C %.2f", c);
             jTextField2.setText(value);      
        }
         if (jComboBox1.getSelectedItem().equals("Fehrenhiet TO Kelvin"))
        {
             double f = Double.parseDouble(jTextField1.getText());
             double k = (f-32)*0.55+273.15;
             String value=String.format("K %.2f", k);
             jTextField2.setText(value);      
        }
         if (jComboBox1.getSelectedItem().equals("Kelvin TO Celsius"))
        {
             double k = Double.parseDouble(jTextField1.getText());
             double c = 0-273.15;
             String value=String.format("째C %.2f", c);
             jTextField2.setText(value);      
        }
        if (jComboBox1.getSelectedItem().equals("Kelvin TO Fehrenhiet"))
        {
             double k = Double.parseDouble(jTextField1.getText());
             double f = (0-273.5)*FC;
             String value=String.format("째F %.2f", f);
             jTextField2.setText(value);      
        } 


    }//GEN-LAST:event_jButton2ActionPerformed

